  public static void main(String[] args) throws IOException {
    final BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
    final PrintWriter pw = new PrintWriter(System.out);
    final int t = Integer.parseInt(br.readLine());
    for (int cnt = 0; cnt < t; cnt++) {
      StringTokenizer st_q = new StringTokenizer(br.readLine());
      long n = Long.parseLong(st_q.nextToken());
      long x = Long.parseLong(st_q.nextToken());
      long k = Long.parseLong(st_q.nextToken());
      String bin_n = Long.toBinaryString(n);
      long max_dist = (bin_n.length() - 1) * 2;
      if (k > max_dist) {
        pw.println(0);
      } else {
        long sum = 0, cnt_p = 0;
        while ((x >> cnt_p) > 0 || k >= cnt_p) {
          sum += func(n, x >> cnt_p, k - cnt_p);
          if (cnt_p > 0 && k > cnt_p) {
            sum -= func(n, x >> (cnt_p - 1), k - cnt_p - 1);
          }
          cnt_p++;
        }
        pw.println(sum);
      }
    }
    pw.close();
    br.close();
  }

  //N頂点の完全二分木において、頂点Xから葉に向かって距離Kの頂点は何個あるか
  static long func(long n, long x, long k) {
    if (k == 0) {
      return 1;
    }
    long min_x = x, max_x = x;
    for (long d = 0; d < k; d++) {
      min_x <<= 1;
      max_x = (max_x << 1) + 1;
      if (n < min_x) {
        return 0;
      }
    }
    max_x = Math.min(n, max_x);
    return max_x - min_x + 1;
  }
}

