class Main {
  static int upperBound(int[] a, int v) {
    int b = 0, e = a.length - 1;

    while (b <= e) {
      int m = (b + e) / 2;
      if (a[m] <= v) b = m + 1;
      else e = m - 1;
    }

    return b;
  }

