import java.io.BufferedReader;
import java.io.InputStreamReader;
import java.util.Arrays;

/**
 * E - Playlist
 * https://atcoder.jp/contests/abc323/tasks/abc323_e
 */
public class Main {

    static long MOD = 998244353;

    public static void main(String[] args) throws Exception {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        String[] input = br.readLine().split(" ");
        int N = Integer.parseInt(input[0]), X = Integer.parseInt(input[1]);
        int[] times = Arrays.stream(br.readLine().split(" ")).mapToInt(Integer::parseInt).toArray();
        long[][] dp = new long[X + 1][N];
        long nInverse = modInverse(N, MOD);
        Arrays.fill(dp[0], 1 * nInverse % MOD);

        long tmp;
        for (int t = 1; t < X + 1; t++) {
            tmp = 0;
            for (int n = 0; n < N; n++) {
                if (t - times[n] < 0) {
                    continue;
                }

                tmp += dp[t - times[n]][n];
                tmp %= MOD;
            }
            Arrays.fill(dp[t], tmp * nInverse % MOD);
        }

        long ans = 0;
        for (int t = 0; t < times[0]; t++) {
            if (X - t < 0) {
                break;
            }
            ans += dp[X - t][0];
            ans %= MOD;
        }

        System.out.println(ans);
    }

    /**
     * lawを法にしたnumの逆元を求める
     * 
     * @param a
     * @param m
     * @return
     */
    static long modInverse(long a, long m) {
        long b = m, u = 1, v = 0;
        long tmp, t;
        while (b != 0) {
            t = a / b;
            a -= t * b;
            tmp = a;
            a = b;
            b = tmp;

            u -= t * v;
            tmp = u;
            u = v;
            v = tmp;
        }
        u %= m;
        if (u < 0) {
            u += m;
        }
        return u;
    }
}

