import java.io.IOException;
import java.io.InputStreamReader;
import java.util.Arrays;

public class Main {
    static int N =  110;
    static int M =  110;
    static int max = 0;
    static int n, m;
    static PII[] a = new PII[M];
    static int[] score = new int[N];
    static boolean[][] st = new boolean[N][M];
    static BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
    public static void main(String[] args) throws IOException {
        String[] strs = br.readLine().split(" ");
        n = Integer.parseInt(strs[0]);
        m = Integer.parseInt(strs[1]);

        strs = br.readLine().split(" ");
        for(int i = 1; i <= m ; i ++) {
            a[i] = new PII(i, Integer.parseInt(strs[i - 1]));
        }

        Arrays.sort(a, 1, m + 1);
        for(int i = 1 ; i <= n ; i++) {
            strs =  br.readLine().split("");
            for(int j = 1 ; j <= m ; j ++) {
                if(strs[j - 1].charAt(0) == 'o') {
                    for(int k = 1 ; k <= m ; k ++) {
                        if(a[k].id == j) {
                            score[i] += a[k].score;
                            break;
                        }
                    }
                    st[i][j] = true;
                }
            }
            score[i] += i;
            max = Math.max(max, score[i]);
        }

        for(int i = 1 ; i <= n ; i ++) {
            if(score[i] == max) {
                System.out.println(0);
            } else {
                int minus = max - score[i];
                int cnt = 0;
                for(int j = 1 ; j <= m ; j ++) {
                    if(minus <= 0) break;
                    if(!st[i][a[j].id]) {
                        minus -= a[j].score;
                        cnt ++;
                    }
                }
                System.out.println(cnt);
            }
        }
    }

    public static class PII implements Comparable<PII> {
        int id;
        int score;

        public PII(int id, int score) {
            this.id = id;
            this.score = score;
        }

        @Override
        public int compareTo(PII o) {
            return o.score - this.score;
        }
    }
}

