		Scanner sc = new Scanner(System.in);
		String T = sc.next();
		int M = T.length();
		int N = sc.nextInt();
		List<List<String>> fukuros = new ArrayList<>();
		for(int i=0;i<N;i++) {
			int A = sc.nextInt();
			List<String> fukuro = new ArrayList<>();
			for(int j=0;j<A;j++) {
				fukuro.add(sc.next());
			}
			fukuros.add(fukuro);
		}

		int[][] dp = new int[N+1][M+1];
		for(int i=0;i<=N;i++) {
			for(int j=0;j<=M;j++) {
				dp[i][j]=INF;
			}
		}
		dp[0][0]=0;

		for(int i=0;i<N;i++) {
			List<String> fukuro = fukuros.get(i);
			for(int j=0;j<=M;j++) {
				dp[i+1][j]=dp[i][j];
