import sys

sys.setrecursionlimit(1000000)
from collections import deque, defaultdict
from heapq import heappop, heappush

def resolve():
    N = int(input())
    products = []
    for _ in range(N):
        t, d = map(int, input().split())
        products.append((t, t + d))

    products.sort()
    todoku = defaultdict(bool)
    packages = deque()
    pret = -1
    cur = []
    for t, d in products:
        todoku[t] = True
        if pret != t:
            if pret != -1: packages.append(cur)
            cur = []
            cur.append((t, d))
            pret = t
        else:
            cur.append((t, d))
    else:
        if cur:
            packages.append(cur)

    canPrint = []
    now = 0
    ans = 0
    while packages or canPrint:
        if not canPrint:
            package = packages.popleft()
            for product in package:
                now = product[0]
                heappush(canPrint, product[1])

            printTar = heappop(canPrint)
            ans += 1
        else:
            now += 1
            if todoku[now]:
                package = packages.popleft()
                for product in package:
                    heappush(canPrint, product[1])

            while canPrint:
                printTar = heappop(canPrint)
                if printTar < now:
                    continue
                else:
                    ans += 1
                    break

    print(ans)

resolve()

